import React, { useState } from 'react';

function ProjectForm() {
  const [newProject, setNewProject] = useState({
    name: '',
    description: '',
    projectId: ''
  });
  const [existingProjectId, setExistingProjectId] = useState('');
  const [response, setResponse] = useState('');

  const handleNewProjectChange = (e) => {
    setNewProject({ ...newProject, [e.target.name]: e.target.value });
  };

  const handleExistingProjectChange = (e) => {
    setExistingProjectId(e.target.value);
  };

  const handleCreateNewProject = async () => {
    try {
      alert('Llega 1');
      const res = await fetch('http://localhost:5000/create_project', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newProject),
      });
      alert('Llega 2');
//      alert(res.json())
 
 //borre        
      const text = await res.text();

 //borre     
      console.log('Raw response:', text);
  
 //borre     
      const data = JSON.parse(text);
      alert(text)
//      const data = await res.json();
      if (res.ok) {
        alert('Llega 3');
        setResponse(data.message);
        alert('Llega 4');
        alert(data.message)
        alert('Llega 5');
        setNewProject({
          name: '',
          description: '',
          projectId: '',
        });
      } else {
        alert('Llega 6');
        setResponse('Error flask connection.');
        alert('Error flask connection.');
      }
    } catch (error) {
      alert('Llega 7');
      setResponse('Error creating project.');
      alert('Error creating project.');
    }
  };

  const handleUseExistingProject = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/projects/${existingProjectId}`);

      const data = await res.json();
      if (res.ok) {
        setResponse(data.message);
      } else {
        setResponse('Error retrieving project.');
      }
    } catch (error) {
      console.error(error);
      setResponse('Error retrieving project.');
    }
  };

  return (
    <div style={{ display: 'flex' }}>
      <div style={{ flex: 1, padding: '10px' }}>
        <h2>Create New Project</h2>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={newProject.name}
            onChange={handleNewProjectChange}
          />
        </label>
        <br />
        <label>
          Description:
          <textarea
            name="description"
            value={newProject.description}
            onChange={handleNewProjectChange}
          />
        </label>
        <br />
        <label>
          Project ID:
          <input
            type="text"
            name="projectId"
            value={newProject.projectId}
            onChange={handleNewProjectChange}
          />
        </label>
        <br />
        <button onClick={handleCreateNewProject}>Create Project</button>
      </div>
      <div style={{ flex: 1, padding: '10px' }}>
        <h2>Use Existing Project</h2>
        <label>
          Project ID:
          <input
            type="text"
            value={existingProjectId}
            onChange={handleExistingProjectChange}
          />
        </label>
        <br />
        <button onClick={handleUseExistingProject}>Use Project</button>
      </div>
    </div>
  );
}

export default ProjectForm;
